import React, { Component } from 'react';
import { connect } from 'react-redux';
import firebase from "firebase";

class Account extends Component {
    style = {
        fontSize: "12pt",
        padding: "5px 10px"
    }

    constructor(props){
        console.log('Account.jsのconstructor')
        super(props);
        this.login_check = this.login_check.bind(this);
        this.match_user = this.match_user.bind(this);
    }

    match_user = (result) => {

        let db = firebase.firestore();
        console.log('in match_user function. result.user.email is');
        console.log(result.user.displayName)
        db.collection("news-user")
        .where('name', '==', result.user.displayName).get().then((docs) => {
            // success
            console.log(doc.data())
            this.props.dispatch({
                type: 'UPDATE_USER',
                value: {
                    login: true,
                    username: result.user.displayName,
                    email: result.user.email,
                    articles : this.props.articles
                }
            });
        }).catch(error => {
            console.log(error);
        })
    }

    login(){
        console.log('Account.jsのlogin()')
        let provider = new firebase.auth.GoogleAuthProvider();
        let self = this;
        let db = firebase.firestore();
        firebase.auth().setPersistence(firebase.auth.Auth.Persistence.LOCAL);
        firebase.auth().signInWithPopup(provider)
        .then((result) => {
            this.match_user(result);
            this.props.onLogined();
        });
    }

    logout(){
        console.log("logout");
        firebase.auth().signOut();
        this.props.dispatch({
            type: 'UPDATE_USER',
            value: {
                login: false,
                username: '(click here!)',
                email: '',
                articles: this.props.articles
            }
        });
        this.props.onLogouted();
    }

    // check if user is logged in or logged out
    login_check(){
        console.log('Account.jsのlogin_check()');
        if (this.props.login === undefined || this.props.login == false){
            this.login();
        } else {
            this.logout();
        }
    }

    render(){
        console.log('Account.jsのrender()');
        firebase.auth().onAuthStateChanged((user) => {
            if (user) {
              // User is signed in.
              this.username_ = user.displayName;
            } else {
              // No user is signed in.
            }
          });

        return (
            <p className="login">
                <span className="acount"
                onClick={this.login_check}>
                    LOGINED: {this.props.username}
                </span>
            </p>
        );
    }
}

Account = connect((state) => state)(Account);
export default Account;
