import React, {Component} from 'react';
import { connect } from 'react-redux';
import Router from 'next/router';
import firebase from "firebase";

class ShareNews extends Component {

    constructor(props){
        super(props);
        if (this.props.login == false) {
            Router.push('/');
        }
        this.state = {
            title: '',
            description: '',
            url: '',
            image: '',
        }
        this.logined = this.logined.bind(this);
        this.doAction = this.doAction.bind(this);
    }

    // login, logout 処理
    logined(){
        console.log('logined.');
    }

    logouted(){
        Router.push('/');
    }

    // データの登録処理
    doAction = (article, userid, e) => {    
        console.log('doActionの中');
        console.log('userid is');
        console.log(userid);
        let db = firebase.firestore();
        // Firestore の登録処理

        db.collection('share').add({
            title: article.title,
            description: article.description,
            image: article.urlToImage,
            url: article.url,
            sharedFrom: userid,
            //sharedTo : 
        })
        .then((doc) => {
            console.log(`共有しました`);
        })
        .catch((error) => {
            console.log(`共有に失敗しました。リトライしてください。`);
        });

        this.setState({
            title: '',
            description: '',
            image: '',
            url: '',
        })
    }

    render(){
        const article = this.props.article;
        const userid = this.props.userid;
        return (
            <div>
                <button onClick={(e) => this.doAction(article, userid, e)}>共有する</button>
            </div>
            // 共有先はここでモーダル（ポータル）を表示して選べるようにする
        );
    }
}

ShareNews = connect((state) => state) (ShareNews);
export default ShareNews;
