import React, {Component} from 'react';
import {connect} from 'react-redux';
import Router from 'next/router';
import Link from 'next/link';

class NewsList extends Component {

    constructor(props){
        super(props);
    }
    /*
    state = {
        articles: [],
        refreshing: false,
    }
    */

    getNews = async () => {
        /*
        this.setState({ refreshing: true });
        */

       this.props.dispatch({
        type: 'UPDATE_USER',
        value: {
            refleshing : true
            }
        });

        // fill your API key!
        const url = "https://newsapi.org/v2/everything?q=mufg&from=2019-12-25&to=2020-01-24&sortBy=popularity&apiKey=30d451b495234aae8b35d83d68082817";
        try {
            const result = await fetch(url);
            const json = await result.json();
            // console.log(json);

            this.props.dispatch({
                type: 'UPDATE_USER',
                value: {
                    articles : json.articles,
                    refleshing : false
                    }
            });

            /*
            this.setState({
                articles: json.articles,
                refreshing: false
            });
            */
        } catch (e) {
            /*
            this.setState({ refreshing: false });
            */
            console.log(e);
        }
    }

    componentDidMount = () => { 
        console.log('component Did Mount!')
        this.getNews();
    }

    render() {
        const itemList = [];
        const items = []
        console.log('inside the render')
        console.log(this.props)
        this.props.articles.map((article, index) => {

            items.push(article);

            itemList.push(
            <li key={index.toString}>
                <Link href="/p/[id]" as={`/p/${index}`}>
                    <a>{article['title']}</a>
                 </Link>
            <ul>
                <li><img src={article['urlToImage']} width="100" height="100" /></li>
                <li>{article['description']}</li>
                <li>{article['content']}</li>
            </ul>
            </li>);
        })

        this.props.dispatch({
            type: 'UPDATE_USER',
            value: {
                itemArray : items
                }
        });

        console.log(this.props.articles);
        return (
            <div>
            <ul>
                {itemList}
            </ul>
            </div>
        );
    }
}

NewsList = connect((state) => state)(NewsList);
export default NewsList;
