import React, {Component} from 'react';
import { connect } from 'react-redux';
import Router from 'next/router';
import firebase from "firebase";

class SelectWho extends Component {

    constructor(props){
        super(props);
        if (this.props.login == false) {
            Router.push('/');
        }
        this.state = {
            userList : []
        }

        this.logined = this.logined.bind(this);
        this.onChangeTitle = this.onChecked.bind(this);
        this.doAction = this.doAction.bind(this);
    }

    // login, logout 処理
    logined(){
        console.log('logined.');
    }

    logouted(){
        Router.push('/');
    }

    onChecked(userid, e) {
        const currentSelectedUsers = this.state.currentSelectedUsers ? this.state.currentSelectedUsers : [];

        e.target.checked ? 
        this.setState({currentSelectedUsers : currentSelectedUsers.push(userid)})
        :
        this.setState({currentSelectedUsers : currentSelectedUsers.filter(elem => elem !== userid)})
    }

    getUserList = (self = this) => {
        let db = firebase.firestore(); // firestore のオブジェクト取得

        db.collection('news-user')
        .get()
        .then(function(querySnapshot) {
            let userList = [];
            querySnapshot.forEach((doc) => {
            // doc.data() is never undefined for query doc snapshots
            console.log(doc.id, " => ", doc.data());
            userList.push(<li key={doc.id}><input type="checkbox" onChange={(e) => this.onChecked(doc.data().userid, e)}>{doc.data().userid}</input></li>);
            })
            self.setState({userList: userList});
        });
    }

    // データの登録処理
    doAction = (article, userid, e) => {    
        console.log('doActionの中');
        console.log('userid is');
        console.log(userid);
        let db = firebase.firestore();
        // Firestore の登録処理

        db.collection('share').add({
            title: article.title,
            description: article.description,
            image: article.urlToImage,
            url: article.url,
            sharedFrom: userid,
            sharedTo : this.state.userList
        })
        .then((doc) => {
            console.log(`共有しました`);
        })
        .catch((error) => {
            console.log(`共有に失敗しました。リトライしてください。`);
        });

        this.setState({
            title: '',
            description: '',
            image: '',
            url: '',
        })
    }

    render(){
        const article = this.props.article;
        const userid = this.props.userid;
        const userList = (this.state.userList === []) || this.getUserList();

        return (
            <div>
                <ul>{userList}</ul>
                <button onClick={(e) => this.doAction(article, userid, e)}>確定</button>
            </div>
            // 共有先はここでモーダル（ポータル）を表示して選べるようにする
        );
    }
}

SelectWho = connect((state) => state) (SelectWho);
export default SelectWho;
