import React, {Component} from 'react';
import {connect} from 'react-redux';
import Link from 'next/link';

class NewsList extends Component {

    constructor(props){
        console.log('constructor now')
        super(props);
    }

    // get data from Firebase
    getNews = async () => {
        console.log('inside getNews')
        const url = "https://newsapi.org/v2/everything?q=mufg&from=2019-12-25&to=2020-01-24&sortBy=popularity&apiKey=30d451b495234aae8b35d83d68082817";
        let self = this;

        const result = await fetch(url);
        const json = await result.json();

        console.log('inside getNews now. json is')
        console.log(json)

        this.props.dispatch({
            type : 'UPDATE_USER',
            value : {
                articles: json.articles
            }
        })
    }

    render() {
        this.props.articles.length === 0 && this.getNews();
        console.log('render now. this.props is')
        console.log(this.props)
        console.log("this.props.articles is")
        console.log(this.props.articles)
        const itemList = [];
        this.props.articles.map((article, index) => {
            itemList.push(
            <li key={index.toString}>
                <Link href="/p/[id]" as={`/p/${index}`}>
                {article['title']}
                </Link>
            <ul>
                <li key="0"><img src={article['urlToImage']} width="100" height="100" /></li>
                <li key="1">{article['description']}</li>
                <li key="2">{article['content']}</li>
            </ul>
            </li>);
        })

        return (
            <div>
            <ul>
                {itemList}
            </ul>
            </div>
        );
    }
}

NewsList = connect((state) => state)(NewsList);
export default NewsList;
